package com.example.pashko00710.yotatest.ui.activity;import android.support.v7.app.AppCompatActivity;import android.widget.EditText;import android.widget.TextView;import com.example.pashko00710.yotatest.R;import com.example.pashko00710.yotatest.utils.RequestCheck;import org.androidannotations.annotations.AfterViews;import org.androidannotations.annotations.Background;import org.androidannotations.annotations.Click;import org.androidannotations.annotations.EActivity;import org.androidannotations.annotations.ViewById;@EActivity(R.layout.activity_main)public class MainActivity extends AppCompatActivity {    EditText url;    @ViewById(R.id.result_text)    TextView resCodeHtml;    @AfterViews    public void ready() {        url = (EditText) findViewById(R.id.my_url);    }    @Click(R.id.clean_url)    public void cleanUrl() {        url.setText("");        resCodeHtml.setText("");    }    @Background    @Click(R.id.new_url)    public void sendUrl() {        final RequestCheck req = new RequestCheck(url.getText().toString());        req.sendUrl();        runOnUiThread(new Runnable() {            @Override            public void run() {                resCodeHtml.setText(req.getFinalCode());            }        });    }}